import React, {useState, useEffect} from 'react'
import { Link, useHistory } from 'react-router-dom'
import SocialService from '../services/SocialService'

const LoginPage = () => {

  const [email, setemail] = useState('')
  const [password, setpassword] = useState('')
  const [social, setsocial] = useState('')
  const history = useHistory();
  let valid = false;

  useEffect(() => {
    SocialService.getAllSocials().then((response) => {
        setsocial(response.data)
        console.log(response.data)
    }).catch(error => {
        console.log(error);
    })
  }, [])


  const getEmail = () => {
      SocialService.getSocialByEmailId(email).then((response) => {
          history.push(``)
      })
  }
  const checkEmail = (e) => {
      e.preventDefault()
      SocialService.getAllSocials().then((response) => {
          social.map((social) => {
            if(email == social.emailId && password == social.password){
                history.push(`/homepage/${social.id}`)
                valid = true;
            }
          })
      if(valid == false)
      {
          history.push('/')
      }
    }).catch(error => {
        console.log(error);
    })
  }
  return (
    <div>
        <th> Enter Email and Password </th>
        <form>
            <div className = "form-group mb-2">
             <label className = "form-label"> Email :</label>
                <input
                     type = "text"
                     placeholder = {"Enter Email"}
                     name = "emailId"
                     className = "form-control"
                     value = {email}
                     onChange = {(e) => setemail(e.target.value)}
                > 
                </input>
            </div>
            <div className = "form-group mb-2">
             <label className = "form-label"> Password :</label>
                <input
                     type = "text"
                     placeholder = {"Enter Password"}
                     name = "password"
                     className = "form-control"
                     value = {password}
                     onChange = {(e) => setpassword(e.target.value)}
                > 
                </input>
            </div>
        </form>
        <button className = "btn btn-success" onClick = {(e) => checkEmail(e)}> Submit</button>
        <Link to = "/">
        <button> Cancel </button>
        </Link>
    </div>
  )
}

export default LoginPage
