import React, {useEffect, useState} from 'react'
import { Link, useParams } from 'react-router-dom'
import PostService from '../services/PostService'
import SocialService from '../services/SocialService'


const Feed = () => {

  const [text, setText] = useState('')
  const [posterName, setPosterName] = useState('')
  const [posts, setPosts] = useState([])
  const {id} = useParams()

  useEffect(() => {
    getAllPosts();
    getPosterName();
  }, [])

  const getAllPosts = () => {
      PostService.getAllPosts().then((response)=>{

          setPosts(response.data)

      }).catch(error => {
            console.log(error)
      })
  }

  const getName = (name, posterId, _callback) => {
      const id = posterId;
      SocialService.getSocialById(id).then((response)=>{
          console.log(response.data.firstName)
          //return <p> {response.data.firstName} </p>
          //return response.data.firstName

          _callback()
      }).catch(error => {
            console.log(error)
      })
  }

  const deletePost = (postId) => {
      PostService.deletePost(postId).then((response)=>{

        console.log(response.data)

      }).catch(error => {
          console.log(error)
      })
      window.location.reload();
  }

  const savePost = (e) => {
      e.preventDefault();
      const posterId = id
      const post = {text, posterId}

      PostService.createPost(post).then((response)=>{

        console.log(response.data)

        }).catch(error => {
            console.log(error)
        })
      window.location.reload();
  }

  const getPosterName = () => {
    PostService.getAllPosts().then((response)=>{

        setPosts(response.data)

    }).catch(error => {
          console.log(error)
    })
  }

  return (
    <div>   
        <div>
            <textarea 
                placeholder = 'Type to create new post...'
                value = {text}
                onChange={(e) => setText(e.target.value)}>
            </textarea>
            <button onClick={(e) => savePost(e)}> Create </button>
            <Link to = {`/homepage/${id}`}>
                <button> Back </button>
            </Link>
        </div>
        <div>
        {console.log(posts)}
            {
                posts.map((post) => {

                    return (<div className = 'post' key = {post.id}>
                        <span> {post.text} </span>
                        <span> {posterName} </span>
                        <button onClick={() => deletePost(post.id)}> Delete </button>
                    </div> )
                })
            }        
        </div>
    </div>
  )
}

export default Feed
